function sortItems(e){var t=document.getElementById("items"),a=t.cloneNode(!1),r=[...t.children];for(item of(r.sort((t,a)=>{var r=1e4*parseInt(t.dataset.year)+100*parseInt(t.dataset.month)+parseInt(t.dataset.day),d=1e4*parseInt(a.dataset.year)+100*parseInt(a.dataset.month)+parseInt(a.dataset.day);return e*(d-r)}),r))a.append(item);t.parentNode.replaceChild(a,t),loadVisibleImages()}window.addEventListener("load",()=>{document.getElementById("sorting-release").addEventListener("click",()=>{itemsOrder=sortItems()});var e=document.getElementById("sorting-release");e.addEventListener("click",()=>{sortItems(parseInt(e.dataset.order)),e.dataset.order*=-1})});